using MixItUp.Base.Model.Web;
using MixItUp.Base.Util;
using MixItUp.Base.Web;
using Newtonsoft.Json.Linq;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Net.Sockets;
using System.Net.WebSockets;
using System.Runtime.Serialization;
using System.Text;
using System.Threading;
using System.Threading.Tasks;

namespace MixItUp.Base.Services.External
{
    [DataContract]
    public class VTubeStudioConnectionModel
    {
        [DataMember]
        public Guid ID { get; set; } = Guid.NewGuid();
        [DataMember]
        public string Name { get; set; }
        [DataMember]
        public string ConnectionURL { get; set; }
        [DataMember]
        public string AuthenticationToken { get; set; }
    }

    public class VTubeStudioModel
    {
        public bool modelLoaded { get; set; }
        public string modelName { get; set; }
        public string modelID { get; set; }
        public VTubeStudioModelPosition modelPosition { get; set; }
    }

    public class VTubeStudioModelPosition
    {
        public double positionX { get; set; }
        public double positionY { get; set; }
        public double rotation { get; set; }
        public double size { get; set; }
    }

    public class VTubeStudioHotKey
    {
        public string name { get; set; }
        public string type { get; set; }
        public string file { get; set; }
        public string hotkeyID { get; set; }

        public string DisplayName
        {
            get
            {
                return !string.IsNullOrEmpty(this.name) ? this.name : this.file;
            }
        }
    }

    public class VTubeStudioWebSocketRequestPacket
    {
        public string apiName { get; set; }
        public string apiVersion { get; set; }
        public string requestID { get; set; }
        public string messageType { get; set; }
        public JObject data { get; set; }

        public VTubeStudioWebSocketRequestPacket() { }

        public VTubeStudioWebSocketRequestPacket(string messageType)
        {
            this.apiName = "VTubeStudioPublicAPI";
            this.apiVersion = "1.0";
            this.requestID = Guid.NewGuid().ToString();
            this.messageType = messageType;
        }

        public VTubeStudioWebSocketRequestPacket(string messageType, JObject data)
            : this(messageType)
        {
            this.data = data;
        }

        public int GetErrorID()
        {
            if (this.data != null && this.data.ContainsKey("errorID"))
            {
                return (int)this.data["errorID"];
            }
            return -1;
        }
    }

    public class VTubeStudioWebSocketResponsePacket : VTubeStudioWebSocketRequestPacket
    {
        public long timestamp { get; set; }
    }

    public class VTubeStudioClient : ServiceClientBase
    {
        public const int MaxCacheDuration = 30;

        public string AuthenticationToken { get; private set; }

        private string connectionURL;

        private AdvancedClientWebSocket webSocket = new AdvancedClientWebSocket();

        private Dictionary<string, VTubeStudioWebSocketResponsePacket> responses = new Dictionary<string, VTubeStudioWebSocketResponsePacket>();

        private bool authenticated = false;

        private IEnumerable<VTubeStudioModel> allModelsCache;
        private DateTimeOffset allModelsCacheExpiration = DateTimeOffset.MinValue;

        private Dictionary<string, IEnumerable<VTubeStudioHotKey>> modelHotKeyCache = new Dictionary<string, IEnumerable<VTubeStudioHotKey>>();
        private DateTimeOffset modelHotKeyCacheExpiration = DateTimeOffset.MinValue;

        public override bool IsConnected { get { return this.webSocket != null && this.webSocket.IsOpen() && this.authenticated; } }

        public VTubeStudioClient(string connectionURL, string authenticationToken)
        {
            this.connectionURL = connectionURL;
            this.AuthenticationToken = authenticationToken;

            this.webSocket.Disconnected += WebSocket_Disconnected;
            this.webSocket.PacketReceived += WebSocket_PacketReceived;
            this.webSocket.PacketSent += WebSocket_PacketSent;
        }

        public override async Task<Result> Connect(CancellationToken cancellationToken)
        {
            if (await this.webSocket.Connect(this.connectionURL, cancellationToken))
            {
                if (string.IsNullOrEmpty(this.AuthenticationToken))
                {
                    VTubeStudioWebSocketResponsePacket response = await this.SendAndReceive(new VTubeStudioWebSocketRequestPacket("APIStateRequest"));
                    if (response != null && response.data != null && response.data.ContainsKey("active"))
                    {
                        JObject data = new JObject();
                        data["pluginName"] = VTubeStudioService.PluginName;
                        data["pluginDeveloper"] = VTubeStudioService.PluginDeveloper;
                        data["pluginIcon"] = VTubeStudioService.PluginIcon;

                        response = await this.SendAndReceive(new VTubeStudioWebSocketRequestPacket("AuthenticationTokenRequest", data), 60);
                        if (response != null && response.data != null && response.data.ContainsKey("authenticationToken"))
                        {
                            this.AuthenticationToken = response.data["authenticationToken"].ToString();
                        }
                    }
                }

                if (!string.IsNullOrEmpty(this.AuthenticationToken))
                {
                    JObject data = new JObject();
                    data["pluginName"] = VTubeStudioService.PluginName;
                    data["pluginDeveloper"] = VTubeStudioService.PluginDeveloper;
                    data["authenticationToken"] = this.AuthenticationToken;

                    VTubeStudioWebSocketResponsePacket response = await this.SendAndReceive(new VTubeStudioWebSocketRequestPacket("AuthenticationRequest", data));
                    if (response != null && response.data != null && response.data.TryGetValue("authenticated", out JToken authenticated) && authenticated.Value<bool>() == true)
                    {
                        this.authenticated = true;

                        return new Result();
                    }
                }
            }
            return new Result(Resources.VTubeStudioConnectionFailed);
        }

        public override async Task Disconnect()
        {            
            await this.webSocket.Disconnect();

            this.authenticated = false;

            this.responses.Clear();
        }

        public async Task<VTubeStudioModel> GetCurrentModel()
        {
            try
            {
                if (this.IsConnected)
                {
                    VTubeStudioWebSocketResponsePacket response = await this.SendAndReceive(new VTubeStudioWebSocketRequestPacket("CurrentModelRequest"));
                    if (response != null && response.data != null)
                    {
                        VTubeStudioModel result = response.data.ToObject<VTubeStudioModel>();
                        if (result != null && result.modelLoaded)
                        {
                            return result;
                        }
                    }
                }
            }
            catch (Exception ex)
            {
                Logger.Log(ex);
            }
            return null;
        }

        public async Task<IEnumerable<VTubeStudioModel>> GetAllModels()
        {
            try
            {
                if (this.IsConnected)
                {
                    if (this.allModelsCacheExpiration <= DateTimeOffset.Now || this.allModelsCache == null)
                    {
                        VTubeStudioWebSocketResponsePacket response = await this.SendAndReceive(new VTubeStudioWebSocketRequestPacket("AvailableModelsRequest"));
                        if (response != null && response.data != null && response.data.TryGetValue("availableModels", out JToken models) && models is JArray)
                        {
                            List<VTubeStudioModel> results = new List<VTubeStudioModel>();
                            foreach (VTubeStudioModel model in ((JArray)models).ToTypedArray<VTubeStudioModel>())
                            {
                                if (model != null)
                                {
                                    results.Add(model);
                                }
                            }
                            this.allModelsCache = results;
                            this.allModelsCacheExpiration = DateTimeOffset.Now.AddMinutes(MaxCacheDuration);
                        }
                    }
                }

                if (this.allModelsCache != null)
                {
                    return this.allModelsCache;
                }
            }
            catch (Exception ex)
            {
                Logger.Log(ex);
            }
            return new List<VTubeStudioModel>();
        }

        public async Task<bool> LoadModel(string modelID)
        {
            JObject data = new JObject();
            data["modelID"] = modelID;

            try
            {
                VTubeStudioWebSocketResponsePacket response = await this.SendAndReceive(new VTubeStudioWebSocketRequestPacket("ModelLoadRequest", data));
                if (response != null && response.data != null && response.data.ContainsKey("modelID"))
                {
                    return true;
                }
            }
            catch (Exception ex)
            {
                Logger.Log(ex);
            }

            return false;
        }

        public async Task<bool> MoveModel(double timeInSeconds, bool relative, double? x, double? y, double? rotation, double? size)
        {
            JObject data = new JObject();
            data["timeInSeconds"] = timeInSeconds;
            data["valuesAreRelativeToModel"] = relative;
            if (x.HasValue) { data["positionX"] = x; }
            if (y.HasValue) { data["positionY"] = y; }
            if (rotation.HasValue) { data["rotation"] = rotation; }
            if (size.HasValue) { data["size"] = size; }

            try
            {
                VTubeStudioWebSocketResponsePacket response = await this.SendAndReceive(new VTubeStudioWebSocketRequestPacket("MoveModelRequest", data));
                if (response != null && response.data != null)
                {
                    return true;
                }
            }
            catch (Exception ex)
            {
                Logger.Log(ex);
            }

            return false;
        }

        public async Task<IEnumerable<VTubeStudioHotKey>> GetHotKeys(string modelID = null)
        {
            try
            {
                if (this.IsConnected)
                {
                    if (this.modelHotKeyCacheExpiration <= DateTimeOffset.Now || !this.modelHotKeyCache.ContainsKey(modelID))
                    {
                        VTubeStudioWebSocketRequestPacket packet = new VTubeStudioWebSocketRequestPacket("HotkeysInCurrentModelRequest");
                        if (!string.IsNullOrEmpty(modelID))
                        {
                            packet.data = new JObject();
                            packet.data["modelID"] = modelID;
                        }

                        VTubeStudioWebSocketResponsePacket response = await this.SendAndReceive(packet);
                        if (response != null && response.data != null && response.data.TryGetValue("availableHotkeys", out JToken hotKeys) && hotKeys is JArray)
                        {
                            List<VTubeStudioHotKey> results = new List<VTubeStudioHotKey>();
                            foreach (VTubeStudioHotKey hotKey in ((JArray)hotKeys).ToTypedArray<VTubeStudioHotKey>())
                            {
                                if (hotKey != null)
                                {
                                    results.Add(hotKey);
                                }
                            }
                            this.modelHotKeyCache[modelID] = results;
                            this.modelHotKeyCacheExpiration = DateTimeOffset.Now.AddMinutes(MaxCacheDuration);
                        }
                    }
                }

                if (this.modelHotKeyCache.ContainsKey(modelID))
                {
                    return this.modelHotKeyCache[modelID];
                }
            }
            catch (Exception ex)
            {
                Logger.Log(ex);
            }

            return new List<VTubeStudioHotKey>();
        }

        public async Task<bool> RunHotKey(string hotKeyID)
        {
            JObject data = new JObject();
            data["hotkeyID"] = hotKeyID;

            try
            {
                VTubeStudioWebSocketResponsePacket response = await this.SendAndReceive(new VTubeStudioWebSocketRequestPacket("HotkeyTriggerRequest", data));
                if (response != null && response.data != null && response.data.ContainsKey("hotkeyID"))
                {
                    return true;
                }
            }
            catch (Exception ex)
            {
                Logger.Log(ex);
            }

            return false;
        }

        public void ClearCaches()
        {
            this.allModelsCache = null;
            this.allModelsCacheExpiration = DateTimeOffset.MinValue;

            this.modelHotKeyCache.Clear();
            this.modelHotKeyCacheExpiration = DateTimeOffset.MinValue;
        }

        private async Task<VTubeStudioWebSocketResponsePacket> SendAndReceive(VTubeStudioWebSocketRequestPacket packet, int delaySeconds = 5)
        {
            try
            {
                this.responses.Remove(packet.requestID);

                await this.webSocket.Send(JSONSerializerHelper.SerializeToString(packet));

                int cycles = delaySeconds * 10;
                for (int i = 0; i < cycles && !this.responses.ContainsKey(packet.requestID); i++)
                {
                    await Task.Delay(100);
                }

                this.responses.TryGetValue(packet.requestID, out VTubeStudioWebSocketResponsePacket response);
                return response;
            }
            catch (Exception ex)
            {
                Logger.Log(ex);
            }
            return null;
        }

        private void WebSocket_PacketReceived(object sender, string packet)
        {
            try
            {
                Logger.Log(LogLevel.Debug, "VTube Studio Packet Received - " + packet);

                VTubeStudioWebSocketResponsePacket response = JSONSerializerHelper.DeserializeFromString<VTubeStudioWebSocketResponsePacket>(packet);
                if (response != null && !string.IsNullOrEmpty(response.requestID))
                {
                    this.responses[response.requestID] = response;
                }
            }
            catch (Exception ex)
            {
                Logger.Log(ex);
            }
        }

        private void WebSocket_PacketSent(object sender, string packet)
        {
            Logger.Log(LogLevel.Debug, string.Format("VTube Studio Packet Sent: {0}", packet));
        }

        private void WebSocket_Disconnected(object sender, WebSocketCloseStatus closeStatus)
        {
            ChannelSession.DisconnectionOccurred(Resources.VTubeStudio);

            Task.Run(this.Reconnect);
        }
    }

    public class VTubeStudioService : ServiceBase
    {
        public const string PluginName = "Mix It Up";
        public const string PluginDeveloper = "https://mixitupapp.com/";
        public const string PluginIcon = "";

        private Dictionary<Guid, VTubeStudioClient> clients = new Dictionary<Guid, VTubeStudioClient>();

        public VTubeStudioService() { }

        public override string Name { get { return Resources.VTubeStudio; } }

        public override bool IsEnabled { get { return ChannelSession.Settings.VTubeStudioConnections.Count > 0; } }

        public override bool IsConnected { get; protected set; }

        public override async Task<Result> ManualConnect(CancellationToken cancellationToken)
        {
            if (ChannelSession.Settings.VTubeStudioConnections.Count == 0)
            {
                return new Result(false);
            }

            List<string> failedConnections = new List<string>();
            foreach (var kvp in ChannelSession.Settings.VTubeStudioConnections)
            {
                VTubeStudioConnectionModel connection = kvp.Value;
                try
                {
                    this.clients[connection.ID] = new VTubeStudioClient(connection.ConnectionURL, connection.AuthenticationToken);
                    Result result = await this.ConnectClient(connection.ID, cancellationToken);
                    if (!result.Success)
                    {
                        failedConnections.Add(connection.Name);
                    }
                }
                catch (Exception ex)
                {
                    Logger.Log(ex);
                }
            }

            if (failedConnections.Count == ChannelSession.Settings.VTubeStudioConnections.Count)
            {
                return new Result(Resources.VTubeStudioConnectionFailed);
            }

            this.IsConnected = true;

            return new Result();
        }

        public override async Task Disconnect()
        {
            this.IsConnected = false;

            this.ClearCaches();

            foreach (var kvp in this.clients)
            {
                await kvp.Value.Disconnect();
            }

            this.clients.Clear();
        }

        public override Task Disable()
        {
            ChannelSession.Settings.VTubeStudioConnections.Clear();

            return Task.CompletedTask;
        }

        public bool IsClientConnected(Guid clientID)
        {
            return this.clients.TryGetValue(clientID, out VTubeStudioClient client) && client.IsConnected;
        }

        public async Task<Result> ConnectClient(Guid clientID, CancellationToken cancellationToken)
        {
            if (this.clients.TryGetValue(clientID, out VTubeStudioClient client))
            {
                return await client.Connect(cancellationToken);
            }
            return new Result(false);
        }

        public async Task<VTubeStudioModel> GetCurrentModel(Guid clientID)
        {
            if (this.clients.TryGetValue(clientID, out VTubeStudioClient client))
            {
                return await client.GetCurrentModel();
            }
            return null;
        }

        public async Task<IEnumerable<VTubeStudioModel>> GetAllModels(Guid clientID)
        {
            if (this.clients.TryGetValue(clientID, out VTubeStudioClient client))
            {
                return await client.GetAllModels();
            }
            return new List<VTubeStudioModel>();
        }

        public async Task<bool> LoadModel(Guid clientID, string modelID)
        {
            if (this.clients.TryGetValue(clientID, out VTubeStudioClient client))
            {
                return await client.LoadModel(modelID);
            }
            return false;
        }

        public async Task<bool> MoveModel(Guid clientID, double timeInSeconds, bool relative, double? x, double? y, double? rotation, double? size)
        {
            if (this.clients.TryGetValue(clientID, out VTubeStudioClient client))
            {
                return await client.MoveModel(timeInSeconds, relative, x, y, rotation, size);
            }
            return false;
        }

        public async Task<IEnumerable<VTubeStudioHotKey>> GetHotKeys(Guid clientID, string modelID = null)
        {
            if (this.clients.TryGetValue(clientID, out VTubeStudioClient client))
            {
                return await client.GetHotKeys(modelID);
            }
            return new List<VTubeStudioHotKey>();
        }

        public async Task<bool> RunHotKey(Guid clientID, string hotKeyID)
        {
            if (this.clients.TryGetValue(clientID, out VTubeStudioClient client))
            {
                return await client.RunHotKey(hotKeyID);
            }
            return false;
        }

        public void ClearCaches()
        {
            foreach (VTubeStudioClient client in this.clients.Values.ToList())
            {
                client.ClearCaches();
            }
        }
    }
}
